import React, { useState } from 'react'
import axios from 'axios'

export const AddProducts = () => {

    const [productName, setProductName] = useState('');
    const [productPrice, setProductPrice] = useState('');
    const [productImg, setProductImg] = useState(null);
    const [productDescription, setProductDescription] = useState('');
    const [error, setError] = useState('');



    const [user, setUser] = useState({
        product_name: "",
        product_price: "",
        product_description: ""
      });
     
      const { product_name, product_price, product_description} = user;
       
      const onInputChange = e => {
        setUser({ ...user, [e.target.name]: e.target.value });
      };
       
      const onSubmit = async e => {
        e.preventDefault();
        await axios.post("http://localhost:3000/posts",user);
        alert('Data Inserted');
      };

    return (
        <div className='add-container'>
            <br />
            <h2>ADD PRODUCTS</h2>
            <hr />
            <form autoComplete="off" className='form-group' onSubmit={AddProducts}>
                <label htmlFor="product-name">Tên sản phẩm</label>
                <input type="text" className='form-control' required
                    onChange={e => onInputChange(e)} value={product_name} />
                <br />
                <label htmlFor="product-descriptio">Mô tả sản phẩm</label>
                <input type="text" className='form-control' required
                    onChange={e => onInputChange(e)} value={product_description} />
                <br />
                <label htmlFor="product-price">Giá</label>
                <input type="text" className='form-control' required
                    onChange={e => onInputChange(e)} value={product_price} />
                <br />
                <label htmlFor="product-img">Ảnh sản phẩm</label>
                <input type="file" className='form-control' id="file" required
                    onChange={productImgHandler} />
                <br />
                <button type="submit" className='submit'>Thêm</button>
            </form>
        </div>
    )
}
