import React, { useState, useEffect } from "react";
import axios from "axios";
import { useHistory, useParams } from "react-router-dom";
 
const EditProduct = () => {
   
  let history = useHistory(); //The useHistory hook gives you access to the history instance that you may use to navigate.
  const { id } = useParams();  //The useParams() hook helps us to access the URL parameters from a current route. 
  const [user, setUser] = useState({
    product_name: "",
    product_price: "",
    product_description: ""
  });
 
  const { product_name, product_price, product_description } = user;
  const onInputChange = e => {
    setUser({ ...user, [e.target.name]: e.target.value });
  };
 
  useEffect(() => {
    loadUser();
  }, []);
 
  const onSubmit = async e => {
    e.preventDefault();
    await axios.put(`http://localhost/ci_cart_geek/update-product/update/${id}`, user);
    history.push("/");
  };
 
  const loadUser = async () => {
    const result = await axios.get(`http://localhost/ci_cart_geek/get_product/${id}`);
    setUser(result.data);
  };
  return (
    <div className="add-container">
     <div className="row"> 
      <div className="col-sm-5 col-offset-3 mx-auto shadow p-5">
        <h2 className="text-center mb-4">Edit Product</h2>
        <form onSubmit={e => onSubmit(e)}>
        <div className="form-group">
            <input
              type="text"
              className="form-control form-control-lg"
              placeholder="Id"
              name=""
              value={id}
              onChange={e => onInputChange(e)}
            />
          </div>
          <div className="form-group">
          <label htmlFor="product-name">Tên sản phẩm</label>
            <input
              type="text"
              className="form-control form-control-lg"
              placeholder="Enter Product Name"
              name="product_name"
              value={product_name}
              onChange={e => onInputChange(e)}
            />
          </div>
          <div className="form-group">
          <label htmlFor="product-name">Giá</label>
            <input
              type="text"
              className="form-control form-control-lg"
              placeholder="Enter Product Price"
              name="product_price"
              value={product_price}
              onChange={e => onInputChange(e)}
            />
          </div>
          <div className="form-group">
          <label htmlFor="product-name">Thông tin sản phẩm</label>
            <input
              type="text"
              className="form-control form-control-lg"
              placeholder="Enter Product Description"
              name="product_description"
              value={product_description}
              onChange={e => onInputChange(e)}
            />
          </div>
          <label htmlFor="product-img">Ảnh sản phẩm</label>
                <input type="file" className='form-control' id="file" required
                    onChange={productImgHandler} />
                <br />
          <button className="submit">Cập nhật</button>
        </form>
       </div>
      </div> 
    </div>
  );
};
 
export default EditProduct;
